import * as React from 'react';
import {View,Text,Image,TouchableOpacity} from 'react-native';
import Stars from 'react-native-stars';
import { FontAwesome, MaterialCommunityIcons } from '@expo/vector-icons'; 
import {NativeBaseProvider,TextArea,Input} from "native-base";
import RNPickerSelect from 'react-native-picker-select';




class Comentarios extends React.Component{

    constructor(props){

        super(props);

        this.state={

            unidades:[{label:"kg",value:1},
                   {label:"g",value:2}
                    ]
        }
    }

    render(
        
    ){



        var comentariosList=[];


        this.props.comentarios.forEach(element => {

            comentariosList.push(

                <View style={{flexDirection:"row" , alignItems:"center", marginTop:'5%', marginBottom:'2%', marginHorizontal:'5%'}}>

                    
                    <FontAwesome name="user" size={30} color="black" />

                    <Text style={{fontSize:20, color:"black"}}> {element.nombre} </Text>
                    
                    <NativeBaseProvider>

                        <Input style={{backgroundColor:'#ffff',textAlign:"center"}} mx="1" value={element.cantidad}  />

                    </NativeBaseProvider>

                    <RNPickerSelect style={{width:'40%'}}
                        onValueChange={(value) => console.log(value)}
                        items={this.state.unidades}
                        placeholder="unidad"
                    />


                </View>
                        

            )
            
        });
    
        return (

            
            <View>

                {comentariosList}

            </View>
        )
    }

}

export default Comentarios;
