import React from 'react';
import { Box, Text, Heading, VStack, CheckCircleIcon, Button, HStack, Center, NativeBaseProvider, Icon  } from "native-base";
import { StyleSheet, Image, View} from 'react-native';
import { useNavigation } from '@react-navigation/native';
import {  ButtonFondoBlanco, ButtonFondoRosa } from '../components/ButtonsLogin';

const PerfilScreen = () => {

const navigation = useNavigation();

    return( <View style={styles.container}>

      <View style={{flexDirection:"1" , alignItems:"center", marginTop:'5%', marginBottom:'2%', marginHorizontal:'5%'}}>

      <Text onPress={() => navigation.navigate('ComentarioReceta')} style={{fontSize:20}}> 4.1 </Text>

      </View>
      <View style={{flexDirection:"row" , marginTop:'0%', marginBottom:'2%', marginHorizontal:'5%'}}>

      <Text onPress={() => navigation.navigate('ComentarioReceta')} style={{fontSize:20}}> 4.1 </Text>

      </View>

      </View>
    )};

const styles = StyleSheet.create({

  centerContent: {
    justifyContent:'center',
    alignItems:'top',
    alignContent: 'flex-start',
  }
});
    export default () => {
        return (
          <NativeBaseProvider>
            <Center flex={1} px="3">
                <PerfilScreen />
            </Center>
          </NativeBaseProvider>
        );
    };
