import React, { useState, useEffect } from 'react'
import { StyleSheet, Text, View } from 'react-native';
import { 
  Box,
  Heading,
  VStack, 
  FormControl, 
  Input, 
  Button, 
  Center, 
  NativeBaseProvider, 
  ScrollView} from "native-base";
  import config from "../config/default.json";
  import axios from 'axios'
  import { useNavigation } from '@react-navigation/native';
  import {  ButtonFondoBlanco, ButtonFondoRosa } from '../components/ButtonsLogin';
  
  const CambiarContraseniaScreen = () => {

    const navigation = useNavigation();
    const [ Password    , setPassword] = useState("");
    const [ Password2    , setPassword2] = useState("");
     

  return <Center w="100%">
     <View maxW="400" h="600" style={styles.scrollView} _contentContainerStyle={{
      px: "20px",
      mb: "4",
      minW: "72"
    }}>
      <Box safeArea p="2"  w="100%" maxW="290" py="8">
        <Center>  
        <Heading size="lg" color="coolGray.800" _dark={{
        color: "warmGray.50"
      }} fontWeight="semibold" fontSize="30">
          Cambiar Contraseña
        </Heading>
        </Center>
        <VStack space={3} mt="5">
        <FormControl isRequired>
            <FormControl.Label>Contraseña</FormControl.Label>
            <Input 
              value={Password}
              onChangeText={setPassword}/>
          </FormControl>
          <FormControl isRequired >
            <FormControl.Label>Repetir Contraseña</FormControl.Label>
            <Input 
              value={Password2}
              onChangeText={setPassword2}
            />
        </FormControl>
          <ButtonFondoRosa text="Guardar" onPress={() => navigation.navigate('Perfil')}/>
          <ButtonFondoBlanco text="Cancelar" onPress={() => navigation.navigate('Perfil')}/>
        </VStack>
      </Box>
      </View>
    </Center>;
};

const styles = StyleSheet.create({

  centerContent: {
    justifyContent:'center',
    alignItems:'center'
  },

  scrollView: {
    marginHorizontal: 1,
    marginVertical: 1,
  },
  text: {
    fontSize: 42,
  },
  container: {
    padding:50,
    alignItems: 'center',
    justifyContent: 'center',
  },
});

    export default () => {
        return (
          <NativeBaseProvider>
                <CambiarContraseniaScreen />
          </NativeBaseProvider>
        );
    };
