import * as React from 'react';
import {View,Text,Image,TouchableOpacity} from 'react-native';
import Stars from 'react-native-stars';
import { FontAwesome, MaterialCommunityIcons } from '@expo/vector-icons'; 
import {NativeBaseProvider,TextArea,Input,Select} from "native-base";
import RNPickerSelect from 'react-native-picker-select';




class Ingredients extends React.Component{

    constructor(props){

        super(props);

        this.state={

            unidades:[{label:"kg",value:1},
                   {label:"g",value:2}
                    ],
            unidadSel:""

        }
    }

    render(
        
    ){



        var ingredientsList=[];


        this.props.ingredientes.forEach(element => {

            ingredientsList.push(

                <View style={{flexDirection:"row" , justifyContent:"center",alignItems:"center", marginTop:'5%', marginBottom:'2%', marginLeft:'5%'}}>

                    <Text style={{fontSize:16, width:'60%'}}> {element.nombre} </Text> 

                    <NativeBaseProvider>

                        <Input style={{backgroundColor:'#ffff',textAlign:"center" ,width:'20%'}} mx="1" value={element.cantidad}  />


                        <Select 
                            selectedValue={condition}
                            minWidth="50" 
                            _selectedItem={{
                            bg: "indigo",
                            endIcon: <CheckIcon size={5} />
                            }} 
                            mt="1"
                            onValueChange={itemValue =>setCondition(itemValue)}>
                            {conditionsList.map( (condition) => (
                                <Select.Item key={condition._id} label={condition.desc} value={condition.desc} />
                            ))}          
                        </Select>

                    </NativeBaseProvider>


                </View>
                        

            )


            
        });
    
        return (

            
            <View>

                {ingredientsList}

            </View>
        )


    }

}

export default Ingredients;
