const viewReceta  = ({navigation}) => {

return (
    <ScrollView style={styles.container}>
    
    <Image style={{width: '100%',height: '30%'}}  source ={{uri:tipoImage}}>
                        
    </Image>

     </ScrollView>
  );
};

const styles = StyleSheet.create({
    container: {
      flex: 1,
