import React from 'react';
import { Box, Text, Heading, VStack, CheckCircleIcon, Button, HStack, Center, NativeBaseProvider, Icon  } from "native-base";
import { StyleSheet, Image, View} from 'react-native';
import { useNavigation } from '@react-navigation/native';

const RegisterSuccessScreen = () => {

const navigation = useNavigation();

    return <Center w="100%">
      <Box safeArea p="2" py="8" w="90%" maxW="290">
          <Center>
      <View style={styles.centerContent}>
        <Image
          style={{ width: 100, height: 100, marginBottom: 15 }}
          source = {{ uri: "https://reactjs.org/logo-og.png" }}
        />    
      </View>
      </Center>
        <Heading size="lg" fontWeight="600" color="coolGray.800" _dark={{
        color: "warmGray.50"
      }}>
          El Registro Fue Realizado Correctamente
        </Heading>
        <VStack space={3} mt="5">
          <Button mt="2" colorScheme="indigo" onPress={() => { navigation.navigate('Login')}}>
            Ingresar
          </Button>
        </VStack>
      </Box>
    </Center>;
};

const styles = StyleSheet.create({

  centerContent: {
    justifyContent:'center',
    alignItems:'center'
  }
});
    export default () => {
        return (
          <NativeBaseProvider>
            <Center flex={1} px="3">
                <RegisterSuccessScreen />
            </Center>
          </NativeBaseProvider>
        );
    };
