import React from 'react';
import { Box, Text, Heading, VStack, CheckCircleIcon, Button, HStack, Center, NativeBaseProvider, Icon  } from "native-base";
import { StyleSheet, Image, View} from 'react-native';
import { useNavigation } from '@react-navigation/native';
import {  ButtonFondoBlanco, ButtonFondoRosa } from '../components/ButtonsLogin';

const InicioScreen = () => {

const navigation = useNavigation();
const [ nickname , setUsuario] = useState("");
const [ mail    , setEmail] = useState("");

const baseUrl =  config.baseUrl;
const Inicio = async () => {

  const setup = {
    headers:{
      'content-type' : 'application/json'
    }
  }
  const body = JSON.stringify({nickname, mail})

  try {
    const res = await axios.post(`${baseUrl}/usuario/crearInvitado`,body,setup);
    navigation.navigate('RegisterPassword')
    console.log(res.data);
    console.log(res)
  }catch(error){
    console.log("Here")
    alert("Login fallido")
  }
}
;

