      <Box safeArea p="1" py="1" w="100%" maxW="290" marginBottom= "50">
      <Center>
        <Heading size="lg" color="coolGray.800" _dark={{
        color: "warmGray.50"
      }} fontWeight="semibold" fontSize="50">
          RecetApp
        </Heading>
        </Center> 
      </Box>
      <Box safeArea p="1" py="1" w="100%" maxW="290">
          <Center>
      <View style={styles.centerContent}>
        <Image
          style={{ width: 150, height: 160, marginBottom: 100 }}
          source = { require('../assets/logo.png') }
        />   
      </View>
      </Center>
        <VStack space={3} mt="5">
        <ButtonFondoRosa text="Iniciar SesiÃ³n" onPress={Inicio}/>
        <ButtonFondoBlanco text="Registrate" onPress={() => navigation.navigate('Register')}/>
        </VStack>
      </Box>
    </Center>;
};

const styles = StyleSheet.create({

  container: {
    flex: 1,
    backgroundColor: '#FBE192',
  },

  centerContent: {
    justifyContent:'center',
    alignItems:'center'
  }
});
    export default () => {
        return (
          <NativeBaseProvider>
            <Center style={styles.container}  flex={1} px="3">
                <InicioScreen />
            </Center>
          </NativeBaseProvider>
        );
    };
