import * as React from 'react';
import {View,Text,Image,TouchableOpacity} from 'react-native';
import Stars from 'react-native-stars';
import { FontAwesome, MaterialCommunityIcons } from '@expo/vector-icons'; 


class Recetas extends React.Component{

    render(){
        const {tipo,usuario,tipoImage} = this.props.tipos;
        return (
            
            <TouchableOpacity style={{backgroundColor:'floralwhite'}}>
                
                <View style={{flexDirection:"row",width:'50%',marginVertical:'5%' ,margin:1 }}>

                    <Image style={{resizeMode: 'cover',width: '100%',height: 100}}  source ={{uri:tipoImage}}>
                    
                    </Image>


                    <View style={{width:'100%',alignItems:'left'}}>

                        <Text style={{textAlign:"left",fontWeight:'bold',marginLeft:'15%'}}>
                            {tipo}
                        </Text>

                        <Text style={{color:"#FFD700",textAlign:"left",fontWeight:'bold',marginLeft:'15%',marginVertical:'5%'}}>
                            {usuario}
                        </Text>

                         <View style={{alignItems:"flex-end",flexDirection:"row-reverse"}} >

                            <Stars 
                                display={4.1}
                                spacing={4}
                                count={5}
                                starSize={16}
                                fullStar= {<FontAwesome name="star" color="blue" />}
                                emptyStar= {<FontAwesome name="star-o" color="blue" />}/>

                            <MaterialCommunityIcons name="heart-plus-outline" size={20} color="blue" />

                        </View>


                    </View>


                </View>

            </TouchableOpacity>    

        )
    }

}

export default Recetas;
