import * as React from 'react';
import {View,Text,Image,TouchableOpacity} from 'react-native';
import Stars from 'react-native-stars';
import { FontAwesome, MaterialCommunityIcons } from '@expo/vector-icons'; 
import {NativeBaseProvider,TextArea,Input,Select,CheckIcon,HStack} from "native-base";
import RNPickerSelect from 'react-native-picker-select';




class Steps extends React.Component{

    constructor(props){

        super(props);

      
    }

    render(
        
    ){



        var pasosList=[];
     


        this.props.pasos.forEach((element,index) => {

 
            ingredientsList.push(

                <View>

                <View style={{flexDirection:"row" ,alignItems:"center", marginTop:'5%', marginBottom:'2%', marginLeft:'5%'}}>

                    <Text style={{fontSize:16, width:'60%'}}> {element.idPaso} </Text> 

                    <NativeBaseProvider>

                        <TextArea style={{backgroundColor:"#ffff"}} w ="90%" mx ="5" value={element.descripcion} 
                            placeholder="Disabled TextArea" isDisabled />
                          
                    </NativeBaseProvider>


                </View>

            </View>
                        

            )

            
        });
    
        return (
            
            <View>
                {pasosList}
            </View>
        )


    }

}

export default Steps;
